import React, { useState } from "react";
import Header from "../header";
import PersonalInfo from "../forms/PersonalInfo";
import BusinessProfile from "../forms/BusinessProfile";
import AdditionalUser from "../forms/AdditionalUser";

function MultiPageForm() {
  const [page, setPage] = useState(1);
  const pageTitles = ["Your Profile", "Business Profile", "Additional User"];

  const nextPage = () => setPage((prevPage) => Math.min(prevPage + 1, 3));
  const prevPage = () => setPage((prevPage) => Math.max(prevPage - 1, 1));

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      <Header />
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-2xl">
        <nav className="flex justify-around mb-6 border-b pb-4">
          {pageTitles.map((title, index) => (
            <span
              key={index}
              className={`text-lg font-semibold ${
                page === index + 1 ? "text-blue-500" : "text-gray-400"
              }`}
            >
              {title}
            </span>
          ))}
        </nav>

        {page === 1 && <PersonalInfo nextPage={nextPage} />}
        {page === 2 && <BusinessProfile nextPage={nextPage} prevPage={prevPage} />}
        {page === 3 && <AdditionalUser prevPage={prevPage} />}

        <div className="mt-6 flex justify-between items-center">
          <button
            onClick={() => setPage(1)}
            className="text-blue-500 hover:underline"
          >
            Back to Login
          </button>
          <button
            onClick={page < 3 ? nextPage : undefined}
            className="bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600"
          >
            {page < 3 ? "Next Step" : "Submit"}
          </button>
        </div>
      </div>
    </div>
  );
}

export default MultiPageForm;
