import React from "react";

function PersonalInfo({ nextPage }) {
  return (
    <div className="h-full flex flex-col justify-between">
      <div>
        <h2 className="text-xl font-semibold mb-4 text-blue-600">Step 1</h2>
        <p className="text-gray-600 mb-6">Please, enter your profile information below.</p>
        <form>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-gray-700">First Name</label>
              <input type="text" className="w-full px-4 py-2 border rounded-lg" />
            </div>
            <div>
              <label className="block text-gray-700">Last Name</label>
              <input type="text" className="w-full px-4 py-2 border rounded-lg" />
            </div>
            <div className="col-span-2">
              <label className="block text-gray-700">Email</label>
              <input type="email" className="w-full px-4 py-2 border rounded-lg" />
            </div>
          </div>
        </form>
      </div>
      <button
        type="button"
        onClick={nextPage}
        className="bg-blue-500 text-white px-4 py-2 mt-6 rounded hover:bg-blue-600"
      >
        Next Step
      </button>
    </div>
  );
}

export default PersonalInfo;
