import React, { useState, useEffect, useRef } from "react";
import PersonalInfo from "./PersonalInfo";
import BusinessProfile from "./BusinessProfile";
import AdditionalUser from "./AdditionalUser";

function MultiPageForm() {
  const [page, setPage] = useState(1);
  const cardRef = useRef(null);

  const nextPage = () => setPage(page + 1);
  const prevPage = () => setPage(page - 1);

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 to-gray-700 flex flex-col items-center justify-center">
      {/* Header */}
      <h1 className="text-3xl font-bold text-white mb-8">Create New Account</h1>

      {/* Card */}
      <div ref={cardRef} className="bg-white shadow-lg w-full max-w-lg p-6">
        {/* Stepper Navigation */}
        <div className="flex border-b mb-6">
          {['1 Your Profile', '2 Business Information', '3 Additional Users'].map((step, index) => (
            <div
              key={index}
              className={`flex-1 text-center cursor-pointer py-2 ${page === index + 1 ? "bg-blue-500 text-white" : "bg-gray-200"}`}
              onClick={() => setPage(index + 1)}
            >
              {step}
            </div>
          ))}
        </div>

        {/* Page Content */}
        <div>
          {page === 1 && <PersonalInfo nextPage={nextPage} />}
          {page === 2 && <BusinessProfile nextPage={nextPage} prevPage={prevPage} />}
          {page === 3 && <AdditionalUser prevPage={prevPage} />}
        </div>

        {/* Footer Navigation */}
        <div className="flex justify-between items-center mt-4">
          <button className="text-blue-500">Back to Login</button>
          <div className="flex gap-4">
            {page > 1 && (
              <button onClick={prevPage} className="bg-gray-300 text-gray-700 px-4 py-2 rounded hover:bg-gray-400">
                Previous Step
              </button>
            )}
            {page < 3 && (
              <button onClick={nextPage} className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
                Next Step
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default MultiPageForm;
