import React, { useState } from "react";
import PersonalInfo from "./PersonalInfo";
import ContactInfo from "./ContactInfo";

function MultiPageForm() {
  const [page, setPage] = useState(1);

  const nextPage = () => setPage(page + 1);
  const prevPage = () => setPage(page - 1);

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <div className="bg-white p-10 rounded-lg shadow-xl w-full max-w-lg md:max-w-2xl lg:max-w-3xl mx-4">
        <h2 className="text-2xl font-semibold mb-4 text-center">Page {page}</h2>

        {/* Render forms based on page */}
        {page === 1 && <PersonalInfo nextPage={nextPage} />}
        {page === 2 && <ContactInfo prevPage={prevPage} />}

        <div className="mt-6 flex justify-between">
          {page > 1 && (
            <button
              onClick={prevPage}
              className="bg-gray-300 text-gray-700 px-6 py-2 rounded hover:bg-gray-400"
            >
              Previous
            </button>
          )}
          {page < 2 && (
            <button
              onClick={nextPage}
              className="bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600"
            >
              Next
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

export default MultiPageForm;
