import React, { useState } from "react";
import PersonalInfo from "./PersonalInfo";
import BusinessProfile from "./BusinessProfile";
import AdditionalUser from "./AdditionalUser";
import Header from "../header"; // Import Header component

function MultiPageForm() {
  const [page, setPage] = useState(1);

  const nextPage = () => setPage(page + 1);
  const prevPage = () => setPage(page - 1);

  return (
    <div className="min-h-screen bg-gradient-to-b from-flex flex-col items-center justify-center">
      {/* Header Centered Above the Card */}
      <div className="w-full text-center mb-10">
        <h1 className="text-3xl font-bold text-white">Create New Account</h1>
      </div>

      {/* Card with Stepper Navigation */}
      <div className="bg-white rounded-lg shadow-lg w-full max-w-[1200px] p-20 mt-6 mr-80">
        {/* Stepper Navigation */}
        <div className="flex justify-around mb-6">
          <div className={`step ${page === 1 ? "active" : ""}`}>1 Your Profile</div>
          <div className={`step ${page === 2 ? "active" : ""}`}>2 Business Information</div>
          <div className={`step ${page === 3 ? "active" : ""}`}>3 Additional Users</div>
        </div>

        {/* Current Page Content */}
        <div className="text-center mb-8">
          {page === 1 && <PersonalInfo nextPage={nextPage} />}
          {page === 2 && <BusinessProfile nextPage={nextPage} prevPage={prevPage} />}
          {page === 3 && <AdditionalUser prevPage={prevPage} />}
        </div>

        {/* Footer Navigation */}
        <div className="flex justify-between items-center mt-6">
          <button className="text-blue-500">Back to Login</button>
          <div className="flex gap-4">
            {page > 1 && (
              <button
                onClick={prevPage}
                className="bg-gray-300 text-gray-700 px-4 py-2 rounded hover:bg-gray-400"
              >
                Previous Step
              </button>
            )}
            {page < 3 && (
              <button
                onClick={nextPage}
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
              >
                Next Step
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Contact Us Text at Top Right */}
      <div className="absolute top-0 right-0 p-6 mr-80 text-white">
        <p>Contact Us</p>
      </div>
    </div>
  );
}

export default MultiPageForm;
